mapper.delete:

delete from CD_WED_CATEGORY
where ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}
  and CD_CATEGORY = #{cdCategory,jdbcType=INTEGER}

mapper.update:

update CD_WED_CATEGORY
set DS_CATEGORY = #{dsCategory,jdbcType=VARCHAR},
  NO_ORDER = #{noOrder,jdbcType=INTEGER},
  ID_REF = #{idRef,jdbcType=VARCHAR}
where ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}
  and CD_CATEGORY = #{cdCategory,jdbcType=INTEGER}

mapper.select:

select ID_WEDDING_HALL, CD_CATEGORY, DS_CATEGORY, NO_ORDER, ID_REF
from CD_WED_CATEGORY
where ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}
  and CD_CATEGORY = #{cdCategory,jdbcType=INTEGER}
