mapper.delete:

delete from MY_WED_OPTION
where ID_WEDDING = #{idWedding,jdbcType=BIGINT}
  and ID_OPTION = #{idOption,jdbcType=INTEGER}
  and ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}

mapper.update:

update MY_WED_OPTION
set AM_PRICE = #{amPrice,jdbcType=INTEGER},
  ID_REF = #{idRef,jdbcType=VARCHAR}
where ID_WEDDING = #{idWedding,jdbcType=BIGINT}
  and ID_OPTION = #{idOption,jdbcType=INTEGER}
  and ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}

mapper.select:

select ID_WEDDING, ID_OPTION, ID_WEDDING_HALL, AM_PRICE, ID_REF
from MY_WED_OPTION
where ID_WEDDING = #{idWedding,jdbcType=BIGINT}
  and ID_OPTION = #{idOption,jdbcType=INTEGER}
  and ID_WEDDING_HALL = #{idWeddingHall,jdbcType=INTEGER}
